// src/components/Map.js
import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import './map.css';
import 'leaflet/dist/leaflet.css';

const Map = ({ latitude, longitude, zoomLevel = 13 }) => {
    const mapRef = useRef(null);  // Store a reference to the map container

    useEffect(() => {
        if (mapRef.current && !mapRef.current._leaflet_id) {
            // Initialize the map only if it's not already initialized
            const map = L.map(mapRef.current, {
                center: [latitude, longitude],
                zoom: zoomLevel,
            });

            // Add the OpenStreetMap tile layer
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
            }).addTo(map);

            // Add a marker at the given coordinates
            L.marker([latitude, longitude]).addTo(map)
                .bindPopup('<b>You are here!</b>')
                .openPopup();
        }
    }, [latitude, longitude, zoomLevel]);

    return (
        <div
            ref={mapRef}  // Attach map container reference
            style={{height: '100%', width: '100%'}}
        >
        </div>
    );
};

export default Map;
