import React, {useEffect, useState} from 'react';
import SkewedDiv from "../../components/Container/container";
import SponsorSlideshow from "../../components/SponsorSlideShow/sponsor-slideshow";
import './Sponsors.css';
import PageTitle from "../../components/PageTitle/page-title";
import Parallax from "../../components/Parallax/parallax";


function Sponsors() {
    const [sponsorData, setSponsorData] = useState([]);
    const [loading, setLoading] = useState(true);


    const loadSponsors = async () => {
        try {
            const response = await fetch(`${process.env.PUBLIC_URL}/json/sponsors.json`);
            const data = await response.json();
            setSponsorData(data);
            setLoading(false);
        } catch (error) {
            console.error('Error loading sponsors:', error);
            setLoading(false);
        }
    };


    useEffect(() => {
        loadSponsors();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="reset-styles">

            <PageTitle
                title="Sponsors"
                description="Heat Robotics is proud to partner with a variety of sponsors who support our mission. Their contributions help us bring our innovative robot designs to life and make a real impact on firefighter safety.">
            </PageTitle>

            <Parallax image={`wildfire-1.jpg`} />

            <SkewedDiv>
                {sponsorData.map((data, index) => (
                    <SponsorSlideshow key={index} tier={data.tier} sponsors={data.sponsors} />
                ))}
            </SkewedDiv>

            <Parallax image={`wildfire-3.jpg`} />

        </div>
    );
}

export default Sponsors;
